import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { fetchQuestion, updateScore, fetchUserScore } from "../services/apiService";
import "../styles/Game.css";
import { Menu } from "lucide-react";
import { MusicContext } from "../context/MusicProvider";
import { Home as HomeIcon, Lightbulb } from "lucide-react"; // ‚úÖ Lightbulb icon for hints


function Game() {
  const { isMusicOn, startMusic } = useContext(MusicContext);
  const [difficulty] = useState(localStorage.getItem("selectedDifficulty") || "Easy");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [timeLeft, setTimeLeft] = useState(difficulty === "Easy" ? 30 : difficulty === "Medium" ? 20 : 10);
  const [score, setScore] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [isGameOver, setIsGameOver] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showPopUp, setShowPopUp] = useState(false);
  const [hint, setHint] = useState("");
  const [hintUsed, setHintUsed] = useState(false);
  const [showNextButton, setShowNextButton] = useState(false);
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId");

  const timerRef = useRef(null);

  useEffect(() => {
    if (!isMusicOn && startMusic) {
      startMusic();
    }

    fetchNewQuestion();
    fetchUserTotalScore();

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          setIsGameOver(true);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timerRef.current);
  }, [isMusicOn, startMusic]);

  const fetchNewQuestion = async () => {
    try {
      const data = await fetchQuestion();
      setQuestion(data.question);
      setAnswer(data.solution);
      setIsCorrect(null);
      setShowPopUp(false);
      setHint("");
      setHintUsed(false);
      setShowNextButton(false);
      setUserAnswer("");
      setTimeLeft(difficulty === "Easy" ? 30 : difficulty === "Medium" ? 20 : 10);
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const fetchUserTotalScore = async () => {
    if (!userId) return;
    try {
      const data = await fetchUserScore(userId);
      setTotalScore(data.totalScore);
    } catch (error) {
      console.error("Error fetching total score", error);
    }
  };

  const handleNumberClick = (num) => {
    setUserAnswer((prev) => prev + num.toString());
  };

  const checkAnswer = async () => {
    if (parseInt(userAnswer) === parseInt(answer)) {
      const updatedScore = score + 10;
      setScore(updatedScore);
      setTotalScore((prev) => prev + 10);
      await updateScore(userId, updatedScore);
      setIsCorrect(true);
      setShowNextButton(true);
    } else {
      setIsCorrect(false);
    }
    setShowPopUp(true);
  };

  const useHint = () => {
    if (!hintUsed && answer) {
      let lowerBound = Math.max(0, answer - 3);
      let upperBound = answer + 3;
      setHint(`It's greater than ${lowerBound} but less than ${upperBound}`);
      setHintUsed(true);
      setScore((prev) => (prev >= 10 ? prev - 10 : 0));
    }
  };

  const resetGame = () => {
    setIsGameOver(false);
    setScore(0);
    fetchNewQuestion();
  };

  return (
    <div className="Gbody">
      <div className="GameContainer">
        <div className="overlayG">
          {/* Main Menu Button */}
          <button
            className="mainmenu-icon"
            style={{
              position: "absolute",
              top: "20px",
              right: "50px",
              cursor: "pointer",
              background: "orange",
              borderRadius: "50%",
              padding: "10px",
            }}
            onClick={() => navigate("/dashboard")}
          >
            <Home size={30} color="white" />
          </button>

          {/* Display Player's Total Score */}
          <div className="total-score" style={{ position: "absolute", top: "20px", left: "50px", fontSize: "20px", color: "white", fontWeight: "bold" }}>
            Total Score: {totalScore} üèÜ
          </div>

          {/* Hint Box */}
          {hint && <div className="hint-box">{hint}</div>}

          <h2> FIND THE VALUE OF THE BANANA üçå</h2>

          <div className="game-container">
            <div className="timer-block">Time Left: {timeLeft}s</div>

            <div className="question-container">
              {question && <img src={question} alt="Math Puzzle" className="question-image" />}
            </div>

            {/* Number Pad */}
            <div className="number-pad">
              {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
                <button key={num} onClick={() => handleNumberClick(num)} className="number-button">
                  {num}
                </button>
              ))}
            </div>

            {/* Submit Answer Button */}
            <button onClick={checkAnswer} className="submit-button" disabled={!userAnswer}>
              Submit
            </button>

            {/* Hint Button */}
            <button onClick={useHint} className="hint-button">
              <Lightbulb size={20} style={{ marginRight: "5px" }} />
              Use Hint
            </button>

            {/* Popup Message */}
            {showPopUp && (
              <div className="popup">
                {isCorrect ? (
                  <>
                    <p>üéâ Correct! Great Job! üéâ</p>
                    {showNextButton && <button onClick={fetchNewQuestion}>Next Question</button>}
                  </>
                ) : (
                  <p>‚ùå Oops! Try Again! ‚ùå</p>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Game;
