import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Game = () => {
  const location = useLocation();
  const level = location.state?.level || 'Easy';
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(60);

  useEffect(() => {
    fetchQuestion();
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const fetchQuestion = async () => {
    try {
      const response = await axios.get('http://marcconrad.com/uob/banana/api.php?out=json');
      setQuestion(response.data.question);
    } catch (error) {
      console.error('Error fetching question:', error);
    }
  };

  const handleSubmit = () => {
    if (answer.trim() === '') {
      alert('Please enter an answer');
      return;
    }
    // Here you can add answer validation logic
    setScore(score + 10);
    setAnswer('');
    fetchQuestion();
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Find the Value of Banana</h2>
      <p>Difficulty Level: {level}</p>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '50px', marginBottom: '20px' }}>
        <div>
          <h3>Time Left</h3>
          <p>{timeLeft} sec</p>
        </div>
        <div>
          <h3>Score</h3>
          <p>{score}</p>
        </div>
      </div>

      <h3>Question:</h3>
      <p>{question || 'Loading...'}</p>

      <input
        type="text"
        value={answer}
        onChange={(e) => setAnswer(e.target.value)}
        placeholder="Enter your answer"
      />
      <br />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default Game;
