import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/signup.css'; // Importing the signup CSS

const Signup = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/auth/signup', { username, password });
      alert(res.data.message); // Show success message
      navigate('/login'); // Redirect to Login after signup
    } catch (error) {
      alert(error.response?.data?.message || "Signup failed");
    }
  };

  return (
    <div className="Sbody">
    <div className="SignContainer">
    <div className="overlayS">
    
         {/* Back Icon Button */}
         <button className="back-icon" onClick={() => navigate('/')}> 
        <ArrowLeftCircle size={40} />
      </button>
    <div className="signup-container">
      <div className="signup-box">
        <h2 className="signup-title">SIGNUP</h2>
        <form onSubmit={handleSignup}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            className="signup-input"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="signup-input"
          />
          <button type="submit" className="signup-button">SIGNUP</button>
        </form>
        <div className="login-redirect">
          Already have an account? <span className="login-link" onClick={() => navigate('/login')}>Login</span>
        </div>
      </div>
    </div>
    </div>
    </div>
    </div>
  );
};


export default Signup;
