// Banana Escape: The Jungle Puzzle - Frontend (React Component)
import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/styles/BananaEscape.css";
import monkeyWaiting from "../assets/images/monkey_waiting.png";
import monkeyHappy from "../assets/images/monkey_happy.png";
import monkeyConfused from "../assets/images/monkey_confused.png";
import trapImage from "../assets/images/trap.png";
import bridgeImage from "../assets/images/bridge.png";

const BananaEscapeGame = () => {
  const [level, setLevel] = useState(1);
  const [puzzle, setPuzzle] = useState("");
  const [answer, setAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [points, setPoints] = useState(0);
  const [hint, setHint] = useState("");
  const [monkeyReaction, setMonkeyReaction] = useState(monkeyWaiting);
  const [obstacle, setObstacle] = useState(bridgeImage);

  useEffect(() => {
    startLevel();
  }, []);

  const startLevel = () => {
    axios.get("http://localhost:5000/api/start-level", { params: { userId: "user123" } }).then((res) => {
      setLevel(res.data.level);
      setPuzzle(res.data.puzzle);
      setMonkeyReaction(monkeyWaiting);
      setObstacle(res.data.level % 2 === 0 ? bridgeImage : trapImage); // Switch obstacle
    });
  };

  const handleSubmit = () => {
    axios.post("http://localhost:5000/api/submit-answer", { answer, userId: "user123" }).then((res) => {
      setMessage(res.data.message);
      setPoints(res.data.points);
      if (res.data.level) setLevel(res.data.level);

      if (res.data.message.includes("Correct")) {
        setMonkeyReaction(monkeyHappy);
      } else {
        setMonkeyReaction(monkeyConfused);
      }

      startLevel();
    });
  };

  const getHint = () => {
    axios.get("http://localhost:5000/api/get-hint").then((res) => {
      setHint(res.data.hint);
      setMessage(res.data.message);
      setMonkeyReaction(monkeyWaiting);
    });
  };

  return (
    <div className="game-container">
      <h1 className="game-title">BANANA ESCAPE: THE JUNGLE PUZZLE</h1>
      <p className="level-text">Level: {level}</p>
      <p className="puzzle-text">Puzzle: {puzzle}</p>

      <img src={monkeyReaction} alt="Monkey" className="monkey-image" />
      <img src={obstacle} alt="Obstacle" className="obstacle-image" />

      <input type="number" className="answer-input" value={answer} onChange={(e) => setAnswer(e.target.value)} />
      <button className="game-button" onClick={handleSubmit}>Submit Answer</button>
      <button className="game-button" onClick={getHint}>Get Hint</button>

      <p className="message-text">{message}</p>
      <p className="points-text">Points: {points}</p>
      {hint && <p className="hint-text">Hint: {hint}</p>}
    </div>
  );
};

export default BananaEscapeGame;
