import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchQuestion, updateScore, fetchUserScore } from "../services/apiService"; // ‚úÖ API Service
import "../styles/Game.css";
import { Home as HomeIcon, Lightbulb } from "lucide-react"; // ‚úÖ Lightbulb icon for hints

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const level = location.state?.level || "Easy";

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect if not logged in
    }
  }, [navigate]);

  const getInitialTime = (level) => {
    switch (level) {
      case "Easy":
        return 30;
      case "Medium":
        return 20;
      case "Hard":
        return 10;
      default:
        return 20;
    }
  };

  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [isCorrect, setIsCorrect] = useState(null);
  const [showPopUp, setShowPopUp] = useState(false);
  const [feedback, setFeedback] = useState("");
  const [timeLeft, setTimeLeft] = useState(getInitialTime(level));
  const [hint, setHint] = useState(""); // ‚úÖ State for Hint
  const [hintUsed, setHintUsed] = useState(false);
  const [totalScore, setTotalScore] = useState(0); // ‚úÖ Player's Total Score
  const [showNextQuestion, setShowNextQuestion] = useState(false);

  useEffect(() => {
    fetchNewQuestion();
    fetchPlayerScore(); // ‚úÖ Fetch Player's Current Score from DB

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          alert("Time's up! ‚è∞");
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);
  
    return () => clearInterval(timer); 
  }, []); // ‚úÖ 

  // ‚úÖ Fetch Player's Total Score from API
  const fetchPlayerScore = async () => {
    try {
      const data = await fetchUserScore();
      setTotalScore(data.score); // ‚úÖ Store player's current total score
    } catch (err) {
      console.error("Error fetching total score", err);
    }
  };

  // ‚úÖ Fetch New Question
  const fetchNewQuestion = async () => {
    try {
   
      const data = await fetchQuestion();
      setQuestion(data.question); 
      setAnswer(data.solution);
      setIsCorrect(null);
      setShowPopUp(false);
      setHint(""); // Reset hint
      setHintUsed(false); // Reset hint usage
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  

  // ‚úÖ Handle Answer Submission

// ‚úÖ Updated Answer Submission
const handleSubmit = async () => {
  if (parseFloat(userInput) === parseFloat(answer)) {
    setFeedback("Correct! üéâ");
    setShowNextQuestion(true); // ‚úÖ Show Next Question Button

    try {
      await updateScore(10);
      setTotalScore((prevTotal) => prevTotal + 10);
      fetchPlayerScore();
    } catch (err) {
      console.error("Error updating score", err);
    }
  } else {
    setFeedback("Wrong ‚ùå");
  }
  setUserInput(""); // ‚úÖ Clear input after submission
};

  // ‚úÖ Handle Number Input
  const handleNumberClick = (num) => {
    setUserInput(num);
  };



  // ‚úÖ Provide Odd/Even Hint (Reduce Score by 10)
  const useHint = async () => {
    if (totalScore < 10) {
      alert("You need at least 10 points to use a hint!");
      return;
    }

    const isEven = parseInt(answer) % 2 === 0;
    const hintText = isEven ? "Hint: The answer is an EVEN number! üîµ" : "Hint: The answer is an ODD number! üî¥";
    setHint(hintText);

    try {
      await updateScore(-10); // ‚úÖ Reduce Score by 10 in Database
      fetchPlayerScore(); // ‚úÖ Fetch latest score from DB
    } catch (err) {
      console.error("Error updating score after hint", err);
    }
  };

  return (
    <div className="Gbody">
      <div className="GameContainer">
        <div className="overlayG">
          {/* ‚úÖ Main Menu Button */}
          <button
            className="mainmenu-icon"
            style={{
              position: "absolute",
              top: "20px",
              right: "50px",
              cursor: "pointer",
              background: "orange",
              borderRadius: "50%",
              padding: "10px",
            }}
            onClick={() => navigate("/dashboard")}
          >
            <HomeIcon size={30} color="white" />
          </button>

          {/* ‚úÖ Display Player's Total Score in Top Left */}
          <div className="total-score-block">
          <div className="total-score" style={{ position: "absolute", top: "20px", left: "50px", fontSize: "20px", color: "white", fontWeight: "bold" }}>
            Total Score: {totalScore} üèÜ
          </div>
          </div>

          {/* ‚úÖ Hint Block Positioned at Top Right */}
          {hint && (
            <div className="hint-box">
              {hint}
            </div>
          )}

       
          <div className="game-container">
            <div className="score-timer-container">
              <div className="timer-block">Time Left: {timeLeft}s</div>
            </div>

            <div className="question-container">
              {question && (
                <img src={question} alt="Math Puzzle" className="question-image" />
              )}
            </div>

            {/* ‚úÖ Number Pad */}
            <div className="number-pad">
              {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
                <button
                  key={num}
                  onClick={() => handleNumberClick(num)}
                  className={`number-button ${userInput === num ? "selected" : ""}`}
                >
                  {num}
                </button>
              ))}
            </div>

            {/* ‚úÖ Submit Answer Button */}
            <button onClick={handleSubmit} className="submit-button" disabled={userInput === ""}>
              Submit
            </button>


            {showPopUp && (
        <div className="popup">
          {isCorrect ? (
            <>
              <p>üéâ Correct! Great Job! üéâ</p>
            
              <button onClick={fetchNewQuestion}>Next Question</button>
            </>
          ) : (
            <>
              <p>‚ùå Oops! Try Again! ‚ùå</p>
              <button>Play Again</button>
            </>
          )}
        </div>
      )}

            {/* ‚úÖ Hint Button (Reduces Score by 10) */}
            <div className="hint-boxs">
            <button onClick={useHint} className="hint-button">
              <Lightbulb size={20} style={{ marginRight: "5px" }} />
              Use Hint
            </button>
            </div>

            {/* ‚úÖ Display Hint if Used */}
            {/*hint && <div className="hint-text">{hint}</div>*/}
 
            <p className="feedback">{feedback}</p>
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default Game;
