import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/GameDisplay.css';

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [score, setScore] = useState(0);
  const [bananas, setBananas] = useState([]); // Store bananas as an array
  const [obstacles, setObstacles] = useState([]);
  const [monkeyPos, setMonkeyPos] = useState(50);
  const [isJumping, setIsJumping] = useState(false);
  const username = localStorage.getItem('username');

  useEffect(() => {
    if (!location.state || !location.state.level) {
      navigate('/levels');
    }

    // Generate bananas at random positions
    const bananaInterval = setInterval(() => {
      setBananas((prev) => [
        ...prev,
        { left: Math.random() * 80 + 10, top: Math.random() * 50 + 20 },
      ]);
    }, 3000);

    // Generate falling obstacles
    const obstacleInterval = setInterval(() => {
      setObstacles((prev) => [...prev, { left: 100 }]);
    }, 4000);

    return () => {
      clearInterval(bananaInterval);
      clearInterval(obstacleInterval);
    };
  }, [location, navigate]);

  const collectBanana = async () => {
    setScore((prev) => prev + 10);

    try {
      await axios.post('http://localhost:5000/game/update-score', {
        username,
        score: 10,
      });

      // Remove a collected banana
      setBananas((prev) => prev.slice(1));
    } catch (error) {
      console.error('Error updating score:', error);
    }
  };

  const jump = () => {
    if (!isJumping) {
      setIsJumping(true);
      setMonkeyPos(20);
      setTimeout(() => {
        setMonkeyPos(50);
        setIsJumping(false);
      }, 500);
    }
  };

  return (
    <div className="game-container">
      <h1>üçå Welcome to {location.state.level} Mode üçå</h1>
      <h2 className="score">Score: {score}</h2>
      <h2 className="bananas">Bananas Collected: {bananas.length} üçå</h2>

      <div className="game-area">
        {/* Monkey Character */}
        <img
          src="/assets/monkey.jpg"
          alt="Monkey"
          className={`monkey ${isJumping ? 'jump' : ''}`}
          style={{ bottom: `${monkeyPos}%` }}
        />

        {/* Bananas Appearing in the Game Area */}
        {bananas.map((banana, index) => (
          <img
            key={index}
            src="/assets/banana.jpg"
            alt="Banana"
            className="banana"
            style={{ left: `${banana.left}%`, top: `${banana.top}%` }}
          />
        ))}

        {/* Falling Coconuts */}
        {obstacles.map((obstacle, index) => (
          <img
            key={index}
            src="/assets/coconut.jpg"
            alt="Coconut"
            className="obstacle"
            style={{ left: `${obstacle.left}%` }}
          />
        ))}
      </div>

      <div className="controls">
        <button className="game-action-btn" onClick={collectBanana}>
          üçå Collect Banana
        </button>
        <button className="jump-btn" onClick={jump}>
          üêµ Jump
        </button>
        <button className="quit-btn" onClick={() => navigate('/levels')}>
          üîô Quit
        </button>
      </div>
    </div>
  );
};

export default Game;
