import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchQuestion, updateScore, fetchUserScore } from "../services/apiService"; // ✅ API Service
import "../styles/Game.css";
import { Home as HomeIcon, Lightbulb } from "lucide-react"; // ✅ Lightbulb icon for hints

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const level = location.state?.level || "Easy";

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect if not logged in
    }
  }, [navigate]);

  const getInitialTime = (level) => {
    switch (level) {
      case "Easy":
        return 30;
      case "Medium":
        return 20;
      case "Hard":
        return 10;
      default:
        return 20;
    }
  };

  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [timeLeft, setTimeLeft] = useState(getInitialTime(level));
  const [hint, setHint] = useState(""); // ✅ State for Hint
  const [totalScore, setTotalScore] = useState(0); // ✅ Player's Total Score
  const [showNextQuestion, setShowNextQuestion] = useState(false);

  useEffect(() => {
    fetchNewQuestion();
    fetchPlayerScore(); // ✅ Fetch Player's Current Score from DB

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          alert("Time's up! ⏰");
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // ✅ Fetch Player's Total Score from API
  const fetchPlayerScore = async () => {
    try {
      const data = await fetchUserScore();
      setTotalScore(data.score); // ✅ Store player's current total score
    } catch (err) {
      console.error("Error fetching total score", err);
    }
  };

  // ✅ Fetch New Question
  const fetchNewQuestion = async () => {
    try {
      const data = await fetchQuestion();
      setQuestion(""); // Clear first
      setTimeout(() => setQuestion(data.question), 100); // Set after a short delay
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };
  
  // ✅ Handle Answer Submission

// ✅ Updated Answer Submission
const handleSubmit = async () => {
  if (parseFloat(userInput) === parseFloat(answer)) {
    setFeedback("Correct! 🎉");
    setShowNextQuestion(true); // ✅ Show Next Question Button

    try {
      await updateScore(10);
      setTotalScore((prevTotal) => prevTotal + 10);
      fetchPlayerScore();
    } catch (err) {
      console.error("Error updating score", err);
    }
  } else {
    setFeedback("Wrong ❌");
  }
  setUserInput(""); // ✅ Clear input after submission
};

  // ✅ Handle Number Input
  const handleNumberClick = (num) => {
    setUserInput(num);
  };

// ✅ Fetch New Question & Reset States
const handleNextQuestion = async () => {
  setShowNextQuestion(false); // ✅ Hide Next Question Button
  setFeedback(""); // ✅ Clear feedback message
  setUserInput(""); // ✅ Clear input field
  fetchNewQuestion(); // ✅ Load new question
};

  // ✅ Provide Odd/Even Hint (Reduce Score by 10)
  const useHint = async () => {
    if (totalScore < 10) {
      alert("You need at least 10 points to use a hint!");
      return;
    }

    const isEven = parseInt(answer) % 2 === 0;
    const hintText = isEven ? "Hint: The answer is an EVEN number! 🔵" : "Hint: The answer is an ODD number! 🔴";
    setHint(hintText);

    try {
      await updateScore(-10); // ✅ Reduce Score by 10 in Database
      fetchPlayerScore(); // ✅ Fetch latest score from DB
    } catch (err) {
      console.error("Error updating score after hint", err);
    }
  };

  return (
    <div className="Gbody">
      <div className="GameContainer">
        <div className="overlayG">
          {/* ✅ Main Menu Button */}
          <button
            className="mainmenu-icon"
            style={{
              position: "absolute",
              top: "20px",
              right: "50px",
              cursor: "pointer",
              background: "orange",
              borderRadius: "50%",
              padding: "10px",
            }}
            onClick={() => navigate("/dashboard")}
          >
            <HomeIcon size={30} color="white" />
          </button>

          {/* ✅ Display Player's Total Score in Top Left */}
          <div className="total-score-block">
          <div className="total-score" style={{ position: "absolute", top: "20px", left: "50px", fontSize: "20px", color: "white", fontWeight: "bold" }}>
            Total Score: {totalScore} 🏆
          </div>
          </div>

          {/* ✅ Hint Block Positioned at Top Right */}
          {hint && (
            <div className="hint-box">
              {hint}
            </div>
          )}

       
          <div className="game-container">
            <div className="score-timer-container">
              <div className="timer-block">Time Left: {timeLeft}s</div>
            </div>

            <div className="question-container">
              {question && (
              <img src={question} alt="Math Puzzle" className="question-image" key={question} />

              )}
            </div>

            {/* ✅ Number Pad */}
            <div className="number-pad">
              {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
                <button
                  key={num}
                  onClick={() => handleNumberClick(num)}
                  className={`number-button ${userInput === num ? "selected" : ""}`}
                >
                  {num}
                </button>
              ))}
            </div>

            {/* ✅ Submit Answer Button */}
            <button onClick={handleSubmit} className="submit-button" disabled={userInput === ""}>
              Submit
            </button>

              {/* ✅ Next Question Button (Only appears after correct answer) */}
        {showNextQuestion && (
          <button onClick={handleNextQuestion} className="next-question-button">
            Next Question ➡️
          </button>
        )}

            {/* ✅ Hint Button (Reduces Score by 10) */}
            <div className="hint-boxs">
            <button onClick={useHint} className="hint-button">
              <Lightbulb size={20} style={{ marginRight: "5px" }} />
              Use Hint
            </button>
            </div>

            {/* ✅ Display Hint if Used */}
            {/*hint && <div className="hint-text">{hint}</div>*/}
 
            <p className="feedback">{feedback}</p>
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default Game;
