import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchQuestion, updateScore, fetchUserScore } from "../services/apiService";
import "../styles/Game.css";
import { Home as HomeIcon, Lightbulb } from "lucide-react";

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const level = location.state?.level || "Easy";

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect if not logged in
    }
  }, [navigate]);

  const getInitialTime = (level) => {
    switch (level) {
      case "Easy": return 30;
      case "Medium": return 20;
      case "Hard": return 10;
      default: return 20;
    }
  };

  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(getInitialTime(level));
  const [hint, setHint] = useState("");
  const [totalScore, setTotalScore] = useState(0); 

  useEffect(() => {
    fetchNewQuestion();
    fetchPlayerScore();

    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          alert("Time's up! ⏰");
          fetchNewQuestion(); // ✅ Load new question when time runs out
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // ✅ Fetch Player's Total Score from API
  const fetchPlayerScore = async () => {
    try {
      const data = await fetchUserScore();
      setTotalScore(data.score);
    } catch (err) {
      console.error("Error fetching total score", err);
    }
  };

  // ✅ Fetch New Question (Fix Issue)
  const fetchNewQuestion = async () => {
    try {
      const data = await fetchQuestion();
      setQuestion(data.question);
      setAnswer(data.solution);
      setHint(""); // Reset hint
      setUserInput(""); // Reset input field
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  // ✅ Handle Answer Submission (Fix Total Score Reset)
  const handleSubmit = async () => {
    if (parseFloat(userInput) === parseFloat(answer)) {
      setScore((prevScore) => prevScore + 10);
      setFeedback("Correct! 🎉");

      try {
        await updateScore(10);
        await fetchPlayerScore(); // ✅ Fetch updated total score
      } catch (err) {
        console.error("Error updating score", err);
      }

      // ✅ Delay loading the next question for better UI experience
      setTimeout(() => {
        fetchNewQuestion();
        setFeedback(""); // ✅ Reset feedback after question loads
      }, 500);
      
    } else {
      setFeedback("Wrong ❌");
    }

    setUserInput("");
  };

  // ✅ Handle Number Input
  const handleNumberClick = (num) => {
    setUserInput(num);
  };

  // ✅ Provide Hint (Reduce Score by 10)
  const useHint = async () => {
    if (totalScore < 10) {
      alert("You need at least 10 points to use a hint!");
      return;
    }

    const hintText = answer.toString().substring(0, 1);
    setHint(`Hint: Answer starts with ${hintText}...`);

    setTotalScore((prevTotal) => prevTotal - 10);
    setScore((prevScore) => prevScore - 10);

    try {
      await updateScore(-10);
      await fetchPlayerScore(); // ✅ Fetch updated total score
    } catch (err) {
      console.error("Error updating score after hint", err);
    }
  };

  return (
    <div className="Gbody">
      <div className="GameContainer">
        <div className="overlayG">
          {/* ✅ Main Menu Button */}
          <button
            className="mainmenu-icon"
            style={{ position: "absolute", top: "20px", right: "50px", cursor: "pointer", background: "orange", borderRadius: "50%", padding: "10px" }}
            onClick={() => navigate("/dashboard")}
          >
            <HomeIcon size={30} color="white" />
          </button>

          {/* ✅ Display Player's Total Score in Top Left */}
          <div className="total-score" style={{ position: "absolute", top: "20px", left: "50px", fontSize: "20px", color: "white", fontWeight: "bold" }}>
            Total Score: {totalScore} 🏆
          </div>

          <div className="game-container">
            <div className="score-timer-container">
              <div className="score-block">Score: {score}</div>
              <div className="timer-block">Time Left: {timeLeft}s</div>
            </div>

            <div className="question-container">
              {question && <img src={question} alt="Math Puzzle" className="question-image" />}
            </div>

            {/* ✅ Number Pad */}
            <div className="number-pad">
              {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0].map((num) => (
                <button key={num} onClick={() => handleNumberClick(num)} className={`number-button ${userInput === num ? "selected" : ""}`}>
                  {num}
                </button>
              ))}
            </div>

            {/* ✅ Submit Answer Button */}
            <button onClick={handleSubmit} className="submit-button" disabled={userInput === ""}>
              Submit
            </button>

            {/* ✅ Hint Button */}
            <button onClick={useHint} className="hint-button">
              <Lightbulb size={20} style={{ marginRight: "5px" }} />
              Use Hint (-10 Points)
            </button>

            {/* ✅ Display Hint if Used */}
            {hint && <p className="hint-text">{hint}</p>}

            <p className="feedback">{feedback}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Game;
