import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const Game = () => {
  const location = useLocation();
  const level = location.state?.level || "Easy";
  
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [isCorrect, setIsCorrect] = useState(null);
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(60);
  const [showPopUp, setShowPopUp] = useState(false);

  useEffect(() => {
    fetchQuestion();
    
    // Start countdown timer
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // ‚úÖ Fetch Question from Backend API
  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/banana"); // Request from backend proxy
      setQuestion(res.data.question);
      setAnswer(res.data.solution); // Correct answer (hidden)
      setFeedback("");
      setIsCorrect(null);
      setShowPopUp(false);
    } catch (err) {
      console.error("Error fetching question", err);
      setQuestion("Error loading question");
    }
  };

  // ‚úÖ Handle Answer Submission
  const handleSubmit = () => {
    if (userInput.trim() === "") {
      alert("Please enter an answer!");
      return;
    }

    const isCorrectAnswer = parseFloat(userInput) === parseFloat(answer);
    setIsCorrect(isCorrectAnswer);
    setFeedback(isCorrectAnswer ? "Correct! üéâ" : "Wrong ‚ùå");
    
    if (isCorrectAnswer) {
      setScore(score + 10); // Increase score
    }

    setUserInput(""); // Reset input field
    setShowPopUp(true);

    // Fetch next question after 1.5 seconds
    setTimeout(() => {
      fetchQuestion();
    }, 1500);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Find the Value of Banana üçå</h2>
      <p>Difficulty Level: {level}</p>

      {/* ‚úÖ Score & Timer */}
      <div style={{ display: "flex", justifyContent: "center", gap: "50px", marginBottom: "20px" }}>
        <div>
          <h3>Time Left ‚è≥</h3>
          <p>{timeLeft} sec</p>
        </div>
        <div>
          <h3>Score üèÜ</h3>
          <p>{score}</p>
        </div>
      </div>

      {/* ‚úÖ Display Question Image */}
      <h3>Question:</h3>
      {question ? (
        <img src={question} alt="Math Puzzle" style={{ width: "300px", marginBottom: "20px" }} />
      ) : (
        <p>Loading...</p>
      )}

      {/* ‚úÖ Answer Input Field */}
      <input
        type="number"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        placeholder="Enter your answer"
        style={{ padding: "10px", fontSize: "16px", marginBottom: "10px" }}
      />
      <br />
      
      <button onClick={handleSubmit} style={{ padding: "10px 20px", fontSize: "16px", cursor: "pointer" }}>
        Submit
      </button>

      {/* ‚úÖ Feedback Message */}
      {showPopUp && (
        <div style={{ marginTop: "20px", fontSize: "18px", color: isCorrect ? "green" : "red" }}>
          {feedback}
        </div>
      )}
    </div>
  );
};

export default Game;
