import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css'; // Importing the login CSS

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/auth/login', { username, password });
      
      // ✅ Store JWT token and username
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('username', res.data.username); // Add this line
  
      alert("Login Successful!");
      navigate('/dashboard'); // Redirect to dashboard
    } catch (error) {
      alert(error.response?.data?.message || "Login failed");
    }
  };
  

  return (
    <div className="Sbody">
    <div className="LoginContain">
    <div className="overlayL">
    <div className="login-container">
      <div className="login-box">
        <h2 className="login-title">LOGIN</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            className="login-input"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="login-input"
          />
          <button type="submit" className="login-button">LOGIN</button>
        </form>
        <div className="signup-redirect">
          Don’t have an account? <span className="signup-link" onClick={() => navigate('/signup')}>Signup</span>
        </div>
      </div>
    </div>
    </div>
    </div>
    </div>
  );
};

export default Login;
