// Banana Quest: Smart Puzzle Game - Frontend (React Component)
import React, { useState, useEffect } from "react";
import axios from "axios";

const BananaQuestGame = () => {
  const [level, setLevel] = useState(1);
  const [puzzle, setPuzzle] = useState("");
  const [answer, setAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [points, setPoints] = useState(0);
  const [hint, setHint] = useState("");

  useEffect(() => {
    startLevel();
  }, []);

  const startLevel = () => {
    axios.get("http://localhost:5000/start-level", { params: { userId: "user123" } }).then((res) => {
      setLevel(res.data.level);
      setPuzzle(res.data.puzzle);
    });
  };

  const handleSubmit = () => {
    axios.post("http://localhost:5000/submit-answer", { answer, userId: "user123" }).then((res) => {
      setMessage(res.data.message);
      setPoints(res.data.points);
      if (res.data.level) setLevel(res.data.level);
      startLevel();
    });
  };

  const getHint = () => {
    axios.get("http://localhost:5000/get-hint").then((res) => {
      setHint(res.data.hint);
      setMessage(res.data.message);
    });
  };

  return (
    <div>
      <h1>Banana Quest: Smart Puzzle Game</h1>
      <p>Level: {level}</p>
      <p>Puzzle: {puzzle}</p>
      <input type="number" value={answer} onChange={(e) => setAnswer(e.target.value)} />
      <button onClick={handleSubmit}>Submit Answer</button>
      <button onClick={getHint}>Get Hint</button>
      <p>{message}</p>
      <p>Points: {points}</p>
      {hint && <p>Hint: {hint}</p>}
    </div>
  );
};

export default BananaQuestGame;
