import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Home from './components/Home';
import Signup from './components/Signup';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Game from './components/Game';
import Leaderboard from './components/Leaderboard';
import backgroundMusic from './assets/music/background.mp3'; // ✅ Import Music

const App = () => {
  const [musicOn, setMusicOn] = useState(localStorage.getItem('music') === 'on');
  const audioRef = useRef(new Audio(backgroundMusic));

  // ✅ Play/Pause Music Based on State
  useEffect(() => {
    if (musicOn) {
      audioRef.current.loop = true;
      audioRef.current.volume = 0.5;
      audioRef.current.play().catch(err => console.error("Music Play Error:", err));
    } else {
      audioRef.current.pause();
    }
  }, [musicOn]);

  // ✅ Stop Music When Leaving All Game Pages
  const location = useLocation();
  useEffect(() => {
    const gamePages = ['/dashboard', '/game', '/leaderboard'];
    if (!gamePages.includes(location.pathname)) {
      audioRef.current.pause();
    }
  }, [location.pathname]);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dashboard" element={<Dashboard musicOn={musicOn} setMusicOn={setMusicOn} />} />
        <Route path="/game" element={<Game />} />
        <Route path="/leaderboard" element={<Leaderboard />} />
      </Routes>
    </Router>
  );
};

export default App;
