import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Dashboard.css'; // Importing the dashboard CSS
import { UserCircle, Music } from 'lucide-react'; // ✅ Import Music Icon

const Dashboard = () => {
  const [showLevels, setShowLevels] = useState(false);
  const [showSettings, setShowSettings] = useState(false); // ✅ State for Settings form
  const [selectedLevel, setSelectedLevel] = useState('');
  const [username, setUsername] = useState('');
  const [score, setScore] = useState(0);
  const [musicOn, setMusicOn] = useState(localStorage.getItem('music') === 'on'); // ✅ Music State
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (!token) {
      navigate('/login'); // Redirect if not logged in
    } else {
      setUsername(storedUsername);
      fetchUserScore(token);
    }
  }, [navigate]);

  // ✅ Fetch User's Current Score
  const fetchUserScore = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/auth/user', {
        headers: { Authorization: token },
      });
      setScore(res.data.score);
    } catch (error) {
      console.error('Error fetching user score', error);
    }
  };

  // ✅ Toggle Music State
  const toggleMusic = () => {
    const newMusicState = !musicOn;
    setMusicOn(newMusicState);
    localStorage.setItem('music', newMusicState ? 'on' : 'off');
  };

  // ✅ Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    navigate('/login');
  };

  // ✅ Show Game Levels
  const handlePlayGame = () => {
    setShowLevels(true);
  };

  const handleCloseLevels = () => {
    setShowLevels(false);
  };

  // ✅ Show/Hide Settings Form
  const handleSettings = () => {
    setShowSettings(!showSettings);
  };

  // ✅ Start Game
  const handleStartGame = () => {
    if (selectedLevel) {
      navigate('/game', { state: { level: selectedLevel } });
    } else {
      alert('Please select a difficulty level');
    }
  };

  return (
    <div className="Dbody">
      <div className="SignContainer">
        <div className="overlayD">
          {/* Profile Icon */}
          <div className="profile-icon" style={{ position: 'absolute', top: '20px', right: '50px' }}>
            <UserCircle size={40} />
          </div>
          <p className="un" style={{ fontSize: '14px', marginTop: '5px' }}>{username}</p>

          <div className="dashboard-container">
            <h2>Welcome, {username || 'Player'}!</h2>
            <h2>Your Current Score: {score} 🏆</h2>

            <div className="menu">
              <button className="buttonss" onClick={() => navigate('/leaderboard')}>LEADERBOARD</button>
              <button className="buttonss" onClick={handlePlayGame}>PLAY GAME</button>
              <button className="buttonss" onClick={handleSettings}>SETTINGS</button> {/* ✅ New Settings Button */}
              <button className="buttonss" onClick={handleLogout}>QUIT GAME</button>
            </div>

            {/* ✅ Settings Form */}
            {showSettings && (
              <div className="settings-container">
                <h3>Settings</h3>
                <div className="music-toggle">
                  <Music size={30} style={{ marginRight: '10px' }} />
                  <button className="toggle-button" onClick={toggleMusic}>
                    {musicOn ? 'Music: ON 🎵' : 'Music: OFF 🔇'}
                  </button>
                </div>
              </div>
            )}

            {/* ✅ Level Selection */}
            {showLevels && (
              <>
                <div className="overlay active" onClick={handleCloseLevels}></div>
                <div className="levels active">
                  <span className="close-icon" onClick={handleCloseLevels}>❌</span>
                  <div className="headd"><h3>Select Difficulty Level</h3></div>
                  {['Easy', 'Medium', 'Hard'].map(level => (
                    <label key={level} 
                      className="level-option" 
                      style={{
                        border: selectedLevel === level ? '3px solid brown' : '2px solid transparent',
                        padding: '20px',
                        borderRadius: '10px',
                        margin: '5px',
                        transition: 'all 0.3s ease-in-out',
                        marginLeft: '60px'
                      }}>
                      <input
                        type="radio"
                        value={level}
                        checked={selectedLevel === level}
                        onChange={(e) => setSelectedLevel(e.target.value)}
                      />
                      {level}
                    </label>
                  ))}
                  <div className="btn">
                    <button className="level-button" onClick={handleStartGame}>Start Game</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
