import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Leaderboard.css"; // Import the CSS file
import { fetchLeaderboard } from "../services/leaderboardService"; // ‚úÖ Import API Service

const Leaderboard = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    loadLeaderboard();
  }, []);

  // ‚úÖ Fetch leaderboard using the new service
  const loadLeaderboard = async () => {
    try {
      const data = await fetchLeaderboard();
      setLeaderboard(data);
    } catch (err) {
      console.error("Error loading leaderboard", err);
    }
  };

  return (
    <div className="leaderboard-container">
      <h2 className="leaderboard-title">üèÜ Leaderboard üèÜ</h2>
      <div className="leaderboard-box">
        <table className="leaderboard-table">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Username</th>
              <th>Score</th>
            </tr>
          </thead>
          <tbody>
            {leaderboard.length > 0 ? (
              leaderboard.map((user, index) => (
                <tr key={user._id} className="leaderboard-row">
                  <td>ü•á {index + 1}</td>
                  <td>{user.username}</td>
                  <td>{user.score}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3">No players yet! üò¢</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      <button className="back-button" onClick={() => navigate("/dashboard")}>
        üîô Back to Dashboard
      </button>
    </div>
  );
};

export default Leaderboard;
