import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/GameDisplay.css';

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [score, setScore] = useState(0);
  const [bananas, setBananas] = useState([]);
  const [obstacles, setObstacles] = useState([]);
  const [monkeyPos, setMonkeyPos] = useState({ left: 10, bottom: 10 });
  const [isJumping, setIsJumping] = useState(false);
  const [gameSpeed, setGameSpeed] = useState(3); // Speed of obstacles and monkey movement
  const username = localStorage.getItem('username');

  useEffect(() => {
    if (!location.state || !location.state.level) {
      navigate('/levels');
    }

    // Move the monkey forward automatically
    const moveMonkey = setInterval(() => {
      setMonkeyPos((prev) => ({ ...prev, left: prev.left + 1 }));
    }, 100);

    // Generate bananas at random positions
    const bananaInterval = setInterval(() => {
      setBananas((prev) => [
        ...prev,
        { left: Math.random() * 80 + 20, bottom: Math.random() * 50 + 30 },
      ]);
    }, 3000);

    // Generate falling coconuts
    const obstacleInterval = setInterval(() => {
      setObstacles((prev) => [
        ...prev,
        { left: Math.random() * 80 + 20, bottom: 80 },
      ]);
    }, 4000);

    return () => {
      clearInterval(moveMonkey);
      clearInterval(bananaInterval);
      clearInterval(obstacleInterval);
    };
  }, [location, navigate]);

  // Function to collect bananas when monkey overlaps
  useEffect(() => {
    bananas.forEach((banana, index) => {
      if (
        Math.abs(banana.left - monkeyPos.left) < 10 &&
        Math.abs(banana.bottom - monkeyPos.bottom) < 10
      ) {
        setBananas((prev) => prev.filter((_, i) => i !== index));
        setScore((prev) => prev + 10);

        axios.post('http://localhost:5000/game/update-score', {
          username,
          score: 10,
        }).catch(console.error);
      }
    });
  }, [monkeyPos, bananas, username]);

  // Function to jump
  const jump = () => {
    if (!isJumping) {
      setIsJumping(true);
      setMonkeyPos((prev) => ({ ...prev, bottom: prev.bottom + 30 }));
      setTimeout(() => {
        setMonkeyPos((prev) => ({ ...prev, bottom: 10 }));
        setIsJumping(false);
      }, 500);
    }
  };

  return (
    <div className="game-container">
      <h1>🍌 Welcome to {location.state.level} Mode 🍌</h1>
      <h2 className="score">Score: {score}</h2>

      <div className="game-area">
        {/* Monkey Character */}
        <img
          src="/assets/monkey.jpg"
          alt="Monkey"
          className={`monkey ${isJumping ? 'jump' : ''}`}
          style={{ left: `${monkeyPos.left}%`, bottom: `${monkeyPos.bottom}%` }}
        />

        {/* Bananas */}
        {bananas.map((banana, index) => (
          <img
            key={index}
            src="/assets/banana.jpg"
            alt="Banana"
            className="banana"
            style={{ left: `${banana.left}%`, bottom: `${banana.bottom}%` }}
          />
        ))}

        {/* Falling Coconuts */}
        {obstacles.map((obstacle, index) => (
          <img
            key={index}
            src="/assets/coconut.jpg"
            alt="Coconut"
            className="obstacle"
            style={{ left: `${obstacle.left}%`, bottom: `${obstacle.bottom}%` }}
          />
        ))}
      </div>

      <div className="controls">
        <button className="jump-btn" onClick={jump}>
          🐵 Jump
        </button>
        <button className="quit-btn" onClick={() => navigate('/levels')}>
          🔙 Quit
        </button>
      </div>
    </div>
  );
};

export default Game;
