// Banana Balance Challenge - Frontend (React Component)
import React, { useState, useEffect } from "react";
import axios from "axios";

const BananaBalanceGame = () => {
  const [totalBananas, setTotalBananas] = useState(20);
  const [baskets, setBaskets] = useState({ basket1: 0, basket2: 0, basket3: 0 });
  const [message, setMessage] = useState("");
  const [hint, setHint] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/api//banana-challenge").then((res) => {
      setTotalBananas(res.data.totalBananas);
    });
  }, []);

  const handleChange = (e) => {
    setBaskets({ ...baskets, [e.target.name]: parseInt(e.target.value) });
  };

  const handleSubmit = () => {
    axios
      .post("http://localhost:5000/api//submit-banana", { ...baskets })
      .then((res) => {
        setMessage(res.data.message);
      })
      .catch((err) => console.error(err));
  };

  const getHint = () => {
    axios.get("http://localhost:5000/api//banana-hint").then((res) => {
      setHint(res.data.hint);
      setMessage(res.data.message);
    });
  };

  return (
    <div>
      <h1>Banana Balance Challenge</h1>
      <p>Total Bananas: {totalBananas}</p>
      <p>Distribute bananas into 3 baskets:</p>
      <input type="number" name="basket1" onChange={handleChange} />
      <input type="number" name="basket2" onChange={handleChange} />
      <input type="number" name="basket3" onChange={handleChange} />
      <button onClick={handleSubmit}>Submit</button>
      <button onClick={getHint}>Get Hint (-5 Points)</button>
      <p>{message}</p>
      {hint && <p>Hint: {hint}</p>}
    </div>
  );
};

export default BananaBalanceGame;
