import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const level = location.state?.level || "Easy";

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // Redirect if not logged in
    }
  }, [navigate]);

  const getInitialTime = (level) => {
    switch (level) {
      case "Easy": return 90;
      case "Medium": return 60;
      case "Hard": return 30;
      default: return 60;
    }
  };

  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(getInitialTime(level));

  useEffect(() => {
    fetchQuestion();
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/game/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const handleSubmit = async () => {
    if (parseFloat(userInput) === parseFloat(answer)) {
      setScore(score + 10);
      setFeedback("Correct! üéâ");

      try {
        const token = localStorage.getItem('token');
        await axios.post("http://localhost:5000/game/update-score", 
          { score: 10 }, 
          { headers: { Authorization: token } }
        );
      } catch (err) {
        console.error("Error updating score", err);
      }
    } else {
      setFeedback("Wrong ‚ùå");
    }
    setUserInput("");
    fetchQuestion();
  };

  const handleNumberClick = (num) => {
    setUserInput((prev) => prev + num);
  };

  const handleClear = () => {
    setUserInput("");
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Find the Value of Banana üçå</h2>
      <p>Difficulty Level: {level}</p>
      <h3>Score: {score}</h3>
      <h3>Time Left: {timeLeft} sec</h3>
      {question && <img src={question} alt="Math Puzzle" />}

      <div style={{ display: "flex", justifyContent: "center", flexWrap: "wrap", maxWidth: "200px", margin: "0 auto" }}>
        {[1,2,3,4,5,6,7,8,9,0].map((num) => (
          <button key={num} onClick={() => handleNumberClick(num)} style={{ margin: "5px", padding: "15px", fontSize: "18px" }}>
            {num}
          </button>
        ))}
      </div>

      <button onClick={handleClear} style={{ margin: "10px", padding: "10px", fontSize: "16px" }}>Clear</button>
      <div style={{ margin: "10px", fontSize: "20px" }}>Answer: {userInput}</div>

      <button onClick={handleSubmit}>Submit</button>
      <p>{feedback}</p>
    </div>
  );
};

export default Game;
