import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Dashboard.css'; // Import Dashboard Styles
import { UserCircle, Music, XCircle } from 'lucide-react'; // ‚úÖ Imported Close Icon
import backgroundMusic from '../assets/music/background.mp3'; // ‚úÖ Import Music File

const Dashboard = () => {
  const [showLevels, setShowLevels] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [selectedLevel, setSelectedLevel] = useState('');
  const [username, setUsername] = useState('');
  const [score, setScore] = useState(0);
  const [musicOn, setMusicOn] = useState(localStorage.getItem('music') === 'on'); // ‚úÖ Music State
  const navigate = useNavigate();
  const audioRef = useRef(new Audio(backgroundMusic)); // ‚úÖ Create Audio Reference

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (!token) {
      navigate('/login'); // Redirect if not logged in
    } else {
      setUsername(storedUsername);
      fetchUserScore(token);
    }

    // ‚úÖ Auto-Play Music if Music is ON
    if (musicOn) {
      audioRef.current.loop = true; // Enable loop for continuous playback
      audioRef.current.volume = 0.5; // Set volume level (0.0 - 1.0)
      audioRef.current.play().catch((err) => console.error("Music Play Error:", err));
    }

    return () => {
      audioRef.current.pause(); // ‚úÖ Stop music when component unmounts
    };
  }, [navigate, musicOn]);

  // ‚úÖ Fetch User's Current Score
  const fetchUserScore = async (token) => {
    try {
      const res = await axios.get('http://localhost:5000/auth/user', {
        headers: { Authorization: token },
      });
      setScore(res.data.score);
    } catch (error) {
      console.error('Error fetching user score', error);
    }
  };

  // ‚úÖ Toggle Music State
  const toggleMusic = () => {
    const newMusicState = !musicOn;
    setMusicOn(newMusicState);
    localStorage.setItem('music', newMusicState ? 'on' : 'off');

    if (newMusicState) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
    }
  };

  // ‚úÖ Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    navigate('/login');
    audioRef.current.pause(); // ‚úÖ Stop music when logging out
  };

  // ‚úÖ Show Game Levels
  const handlePlayGame = () => {
    setShowLevels(true);
  };

  const handleCloseLevels = () => {
    setShowLevels(false);
  };

  // ‚úÖ Show/Hide Settings Form
  const handleSettings = () => {
    setShowSettings(true);
  };

  // ‚úÖ Close Settings Form
  const closeSettings = () => {
    setShowSettings(false);
  };

  // ‚úÖ Start Game
  const handleStartGame = () => {
    if (selectedLevel) {
      navigate('/game', { state: { level: selectedLevel } });
    } else {
      alert('Please select a difficulty level');
    }
  };

  return (
    <div className="Dbody">
      {showSettings && <div className="overlay active"></div>}

      <div className="SignContainer">
        <div className="overlayD">
          {/* Profile Icon */}
          <div className="profile-icon" style={{ position: 'absolute', top: '20px', right: '50px' }}>
            <UserCircle size={40} />
          </div>
          <p className="un" style={{ fontSize: '14px', marginTop: '5px' }}>{username}</p>

          <div className="dashboard-container">
            <h2>Welcome, {username || 'Player'}!</h2>
            <h2>Your Current Score: {score} üèÜ</h2>

            <div className="menu">
              <button className="buttonss" onClick={() => navigate('/leaderboard')}>LEADERBOARD</button>
              <button className="buttonss" onClick={handlePlayGame}>PLAY GAME</button>
              <button className="buttonss" onClick={handleSettings}>SETTINGS</button> {/* ‚úÖ New Settings Button */}
              <button className="buttonss" onClick={handleLogout}>QUIT GAME</button>
            </div>

            {/* ‚úÖ Settings Form (Centered Popup) */}
            {showSettings && (
              <div className="settings-overlay">
                <div className="settings-container">
                  <button className="close-settings" onClick={closeSettings}>
                    <XCircle size={30} color="red" />
                  </button>
                  <h3>Settings</h3>
                  <div className="music-toggle">
                    <Music size={30} style={{ marginRight: '10px' }} />
                    <button className="toggle-button" onClick={toggleMusic}>
                      {musicOn ? 'Music: ON üéµ' : 'Music: OFF üîá'}
                    </button>
                  </div>
                </div>
              </div>
            )}

            {/* ‚úÖ Level Selection */}
            {showLevels && (
              <>
                <div className="overlay active" onClick={handleCloseLevels}></div>
                <div className="levels active">
                  <span className="close-icon" onClick={handleCloseLevels}>‚ùå</span>
                  <div className="headd"><h3>Select Difficulty Level</h3></div>
                  {['Easy', 'Medium', 'Hard'].map(level => (
                    <label key={level} 
                      className="level-option" 
                      style={{
                        border: selectedLevel === level ? '3px solid brown' : '2px solid transparent',
                        padding: '20px',
                        borderRadius: '10px',
                        margin: '5px',
                        transition: 'all 0.3s ease-in-out',
                        marginLeft: '60px'
                      }}>
                      <input
                        type="radio"
                        value={level}
                        checked={selectedLevel === level}
                        onChange={(e) => setSelectedLevel(e.target.value)}
                      />
                      {level}
                    </label>
                  ))}
                  <div className="btn">
                    <button className="level-button" onClick={handleStartGame}>Start Game</button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
