import React, { useState, useEffect, useCallback } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { fetchQuestion, updateScore } from "../services/apiService"; // ✅ Uses API Service
import { toast } from "react-toastify";
import { Home } from 'lucide-react'; // ✅ Corrected Import
import "../styles/Game.css"; // ✅ Import CSS

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const level = location.state?.level || "Easy";

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // Redirect if not logged in
    }
  }, [navigate]);

  const getInitialTime = (level) => {
    switch (level) {
      case "Easy": return 30;
      case "Medium": return 20;
      case "Hard": return 10;
      default: return 20;
    }
  };

  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(getInitialTime(level));

  // ✅ Fetch new question from API
  const fetchNewQuestion = useCallback(async () => {
    try {
      const data = await fetchQuestion();
      setQuestion(data.question);
      setAnswer(data.solution);
      setFeedback(""); // Reset feedback on new question
    } catch (error) {
      console.error("Error fetching question", error);
      toast.error("Failed to load question!");
    }
  }, []);

  // ✅ Handle timer countdown
  useEffect(() => {
    fetchNewQuestion();
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          toast.warning("Time's up! ⏰");
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [fetchNewQuestion]);

  // ✅ Handle answer submission
  const handleSubmit = async () => {
    if (parseFloat(userInput) === parseFloat(answer)) {
      setScore(score + 10);
      setFeedback("✅ Correct! 🎉");
      toast.success("Correct Answer! 🎉");

      try {
        await updateScore(10); // ✅ Updates score via API
      } catch (err) {
        console.error("Error updating score", err);
      }
    } else {
      setFeedback("❌ Wrong Answer");
      toast.error("Wrong Answer! ❌");
    }
    setUserInput("");
    fetchNewQuestion();
  };

  return (
    <div className="Gbody">
      <div className="GameContainer">
        <div className="overlayG">
          
          {/* ✅ Main Menu Button */}
          <button 
            className="mainmenu-icon" 
            style={{ position: 'absolute', top: '20px', right: '50px', cursor: 'pointer', background: 'orange', borderRadius: '50%', padding: '10px' }}
            onClick={() => navigate('/dashboard')}
          >
            <Home size={30} color="white" />
          </button>

          <div className="game-container">
            <div className="score-timer-container">
              <div className="score-block">Score: {score}</div>
              <div className="timer-block">Time Left: {timeLeft}s</div>
            </div>

            <div className="question-container">
              {question && <img src={question} alt="Math Puzzle" className="question-image" />}
            </div>

            {/* ✅ Number Input */}
            <input
              type="number"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              className="number-input"
            />

            <button onClick={handleSubmit} className="submit-button" disabled={userInput === ""}>Submit</button>

            <p className="feedback">{feedback}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Game;
