// Banana Quest: Smart Puzzle Game - Frontend (React Component)
import React, { useState, useEffect } from "react";
import axios from "axios";

const BananaQuestGame = () => {
  const [level, setLevel] = useState(1);
  const [puzzle, setPuzzle] = useState("");
  const [answer, setAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [points, setPoints] = useState(0);
  const [hint, setHint] = useState("");
  const [monkeyReaction, setMonkeyReaction] = useState("üêµ"); // Default monkey emoji

  useEffect(() => {
    startLevel();
  }, []);

  const startLevel = () => {
    axios.get("http://localhost:5000/api/start-level", { params: { userId: "user123" } }).then((res) => {
      setLevel(res.data.level);
      setPuzzle(res.data.puzzle);
      setMonkeyReaction("üêµ"); // Reset monkey to waiting state
    });
  };

  const handleSubmit = () => {
    axios.post("http://localhost:5000/api/submit-answer", { answer, userId: "user123" }).then((res) => {
      setMessage(res.data.message);
      setPoints(res.data.points);
      if (res.data.level) setLevel(res.data.level);

      if (res.data.message.includes("Correct")) {
        setMonkeyReaction("üòÉüêµ"); // Happy monkey when correct
      } else {
        setMonkeyReaction("ü§îüêµ"); // Confused monkey when wrong
      }

      startLevel();
    });
  };

  const getHint = () => {
    axios.get("http://localhost:5000/api/get-hint").then((res) => {
      setHint(res.data.hint);
      setMessage(res.data.message);
      setMonkeyReaction("üëâüêµ"); // Monkey pointing to a hint
    });
  };

  return (
    <div style={{ textAlign: "center", backgroundColor: "#4CAF50", padding: "20px", borderRadius: "10px" }}>
      <h1 style={{ color: "white", fontSize: "36px", fontWeight: "bold" }}>BANANA QUEST: SMART PUZZLE GAME</h1>
      <p style={{ fontSize: "24px", color: "yellow" }}>Level: {level}</p>
      <p style={{ fontSize: "22px", color: "white" }}>Puzzle: {puzzle}</p>

      <div style={{ fontSize: "80px", margin: "20px" }}>{monkeyReaction}</div>

      <input type="number" value={answer} onChange={(e) => setAnswer(e.target.value)} />
      <button onClick={handleSubmit}>Submit Answer</button>
      <button onClick={getHint}>Get Hint</button>

      <p style={{ color: "green", fontSize: "20px" }}>{message}</p>
      <p style={{ color: "orange", fontSize: "20px" }}>Points: {points}</p>
      {hint && <p style={{ color: "blue", fontSize: "18px" }}>Hint: {hint}</p>}
    </div>
  );
};

export default BananaQuestGame;
