import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import '../styles/Game.css'; // Importing the CSS for game page

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const level = location.state?.level || "Easy";

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // Redirect if not logged in
    }
  }, [navigate]);

  const getInitialTime = (level) => {
    switch (level) {
      case "Easy": return 90;
      case "Medium": return 60;
      case "Hard": return 30;
      default: return 60;
    }
  };

  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [userInput, setUserInput] = useState("");
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(getInitialTime(level));

  useEffect(() => {
    fetchQuestion();
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const fetchQuestion = async () => {
    try {
      const res = await axios.get("http://localhost:5000/game/banana");
      setQuestion(res.data.question);
      setAnswer(res.data.solution);
    } catch (err) {
      console.error("Error fetching question", err);
    }
  };

  const handleSubmit = async () => {
    if (parseFloat(userInput) === parseFloat(answer)) {
      setScore(score + 10);
      setFeedback("Correct! ðŸŽ‰");

      try {
        const token = localStorage.getItem('token');
        await axios.post("http://localhost:5000/game/update-score", 
          { score: 10 }, 
          { headers: { Authorization: token } }
        );
      } catch (err) {
        console.error("Error updating score", err);
      }
    } else {
      setFeedback("Wrong âŒ");
    }
    setUserInput("");
    fetchQuestion();
  };

  const handleNumberClick = (num) => {
    setUserInput((prev) => prev + num);
  };

  const handleClear = () => {
    setUserInput("");
  };

  return (
    <div className="game-container">
      <div className="score-timer-container">
        <div className="score-block">Score: {score}</div>
        <div className="timer-block">Time Left: {timeLeft}s</div>
      </div>

      <div className="question-container">
        {question && <img src={question} alt="Math Puzzle" className="question-image" />}
      </div>

      <div className="number-pad">
        {[1,2,3,4,5,6,7,8,9,0].map((num) => (
          <button key={num} onClick={() => handleNumberClick(num)} className="number-button">
            {num}
          </button>
        ))}
      </div>

      <button onClick={handleClear} className="clear-button">Clear</button>

      <div className="answer-display">Answer: {userInput}</div>

      <button onClick={handleSubmit} className="submit-button">Submit</button>
      <p className="feedback">{feedback}</p>
    </div>
  );
};

export default Game;
