import React, { useState, useEffect } from "react";
import axios from "axios";

const BananaCodeBreaker = () => {
  const [guess, setGuess] = useState("");
  const [message, setMessage] = useState("");
  const [hints, setHints] = useState({});
  const [points, setPoints] = useState(0);

  useEffect(() => {
    axios.get("/api/start-banana-code?userId=user123").then((res) => {
      setMessage(res.data.message);
    });
  }, []);

  const handleSubmit = () => {
    axios
      .post("/api/submit-banana-code", { guess, userId: "user123" })
      .then((res) => {
        setMessage(res.data.message);
        setHints(res.data.hints || {});
        setPoints(res.data.points);
      })
      .catch((err) => console.error(err));
  };

  const getHint = () => {
    axios.get("/api/banana-code-hint?userId=user123").then((res) => {
      setMessage(res.data.message);
      setPoints(res.data.points);
    });
  };

  return (
    <div>
      <h1>Banana Code Breaker</h1>
      <p>{message}</p>
      <input
        type="number"
        placeholder="Enter a 3-digit guess"
        value={guess}
        onChange={(e) => setGuess(e.target.value)}
      />
      <button onClick={handleSubmit}>Submit Guess</button>
      <button onClick={getHint}>Get Hint (-5 Points)</button>
      <p>Points: {points}</p>
      {hints.sumHint && <p>Hint: {hints.sumHint}</p>}
      {hints.productHint && <p>Hint: {hints.productHint}</p>}
      {hints.evenOddHint && <p>Hint: {hints.evenOddHint}</p>}
    </div>
  );
};

export default BananaCodeBreaker;
