import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [score, setScore] = useState(0);
  const [bananas, setBananas] = useState(10);
  const username = localStorage.getItem('username'); // Get user

  useEffect(() => {
    if (!location.state || !location.state.level) {
      navigate('/levels');
    }
  }, [location, navigate]);

  const collectBanana = async () => {
    setBananas((prev) => prev + 1);
    setScore((prev) => prev + 10);

    try {
      await axios.post('http://localhost:5000/game/update-score', {
        username,
        score: 10
      });
    } catch (error) {
      console.error("Error updating score:", error);
    }
  };

  return (
    <div className="game-container">
      <h1>üçå Welcome to {location.state.level} Mode üçå</h1>
      <h2>Score: {score}</h2>
      <h2>Bananas Collected: {bananas} üçå</h2>

      <button className="game-action-btn" onClick={collectBanana}>üçå Collect Banana</button>
      <button className="quit-btn" onClick={() => navigate('/levels')}>üîô Quit</button>
    </div>
  );
};

export default Game;
